import { IoMessage, IoMessageCode, IoMessageCodeCategory, ToolkitAction } from '../toolkit/cli-io-host';
type Optional<T, K extends keyof T> = Pick<Partial<T>, K> & Omit<T, K>;
type SimplifiedMessage<T> = Omit<IoMessage<T>, 'time'>;
/**
 * Internal helper that processes log inputs into a consistent format.
 * Handles string interpolation, format strings, and object parameter styles.
 * Applies optional styling and prepares the final message for logging.
 */
export declare function formatMessage<T>(msg: Optional<SimplifiedMessage<T>, 'code'>, category?: IoMessageCodeCategory): IoMessage<T>;
/**
 * Creates an error level message.
 * Errors must always have a unique code.
 */
export declare const error: <T>(action: ToolkitAction, message: string, code: IoMessageCode, payload?: T) => IoMessage<T>;
/**
 * Creates a result level message and represents the most important message for a given action.
 *
 * They should be used sparsely, with an action usually having no or exactly one result.
 * However actions that operate on Cloud Assemblies might include a result per Stack.
 * Unlike other messages, results must always have a code and a payload.
 */
export declare const result: <T>(action: ToolkitAction, message: string, code: IoMessageCode, payload: T) => IoMessage<T>;
/**
 * Creates a warning level message.
 */
export declare const warn: <T>(action: ToolkitAction, message: string, code?: IoMessageCode, payload?: T) => IoMessage<T>;
/**
 * Creates an info level message.
 */
export declare const info: <T>(action: ToolkitAction, message: string, code?: IoMessageCode, payload?: T) => IoMessage<T>;
/**
 * Creates a debug level message.
 */
export declare const debug: <T>(action: ToolkitAction, message: string, code?: IoMessageCode, payload?: T) => IoMessage<T>;
/**
 * Creates a trace level message.
 */
export declare const trace: <T>(action: ToolkitAction, message: string, code?: IoMessageCode, payload?: T) => IoMessage<T>;
export {};
